import React from "react"
import Leftpane from "../LeftPane/LeftPane";
import RightPane from "../RightPane/RightPane";
import Popup from "../Popup/Popup";
import "./Dashboard.css";
import flowerImage from "../../img/blauwebloem.jpg";
import pineappleImage from "../../img/blauweananas.jpg";
class Dashboard extends React.Component {

    constructor(props){
        super(props);
        this.state = {productCards: [], open: true};
    }

    onButtonClicked = () => {

    }

    componentDidMount(){
        let productCards = [
            {
                name: "Placeholder"
            },
            {
                name: "Bloemen",
                img: flowerImage
            },
            {
                name: "Ananas",
                img: pineappleImage
            }
        ];
        this.setState({productCards: productCards})

    }
    addButtonClicked = (inputFronPopup) => {
        this.setState({open: !this.state.open});
        console.log(inputFronPopup);
        let toBeAdded = [
            {
                name: "Placeholder"
            }
        ]

        let mergedArrays = this.state.productCards.concat(toBeAdded);
        this.setState({
            productCards: mergedArrays,
            open: !this.state.open,
        })
    }


    render(){
        let navigationListItems = 
        [
            {
                name: "Home",
                message: 0,
            },
            {
                name: "Facturen",
                message: 3,
            },
            {
                name: "Bestellingen",
                message: 0,
            },
            {
                name: "Retour",
                message: 1,
            },
            {
                name: "Contact",
                message: 2,
            }

        ];
        if(this.state.open === true){
            return(
                <article className="dashboard">
                    <Leftpane navigationListItems={navigationListItems}>buttonText = "Go Premium"</Leftpane>
                    <RightPane onButtonClicked={this.onButtonClicked} productCards={this.state.productCards} headerText="Mijn Producten" buttonSymbol="+" buttonText="Voeg een Product toe"></RightPane>
                    <div></div>
                </article>
            );
        }
        return(
            <Popup addButtonClicked={this.addButtonClicked}/>
        )

    }

}

export default Dashboard;
