import React from "react"
import Leftpane from "../LeftPane/LeftPane";
import RightPane from "../RightPane/RightPane";
import "./Dashboard.css";
import flowerImage from "../../img/flowers.jpg";
import pineappleImage from "../../img/ananas.jpg";
class Dashboard extends React.Component {

    constructor(props){
        super(props);
        this.state = {productCards: []};
    }

    onButtonClicked = () => {
        let toBeAdded = [
            {
                name: "Placeholder"
            }
        ]

        let mergedArrays = this.state.productCards.concat(toBeAdded);
        this.setState({
            productCards: toBeAdded
        })
    }

    componentDidMount(){
        let productCards = [
            {
                name: "Placeholder"
            },
            {
                name: "Flowers",
                img: flowerImage
            },
            {
                name: "Ananas",
                img: pineappleImage
            }
        ];
        this.setState({productCards: productCards})

    }


    render(){
        let navigationListItems = 
        [
            {
                name: "Home",
                message: 0,
            },
            {
                name: "Facturen",
                message: 3,
            },
            {
                name: "Bestellingen",
                message: 0,
            },
            {
                name: "Retour",
                message: 1,
            },
            {
                name: "Contact",
                message: 2,
            }

        ];
        return(
            <article className="dashboard">
                <Leftpane navigationListItems={navigationListItems}>buttonText = "Go Premium"</Leftpane>
                <RightPane onButtonClicked={this.onButtonClicked} productCards={this.state.productCards} headerText="Mijn Producten" buttonSymbol="+" buttonText="Voeg een Product toe"></RightPane>
                <div></div>
            </article>
        );
    }

}

export default Dashboard;
